mapper.select:
  
    select pono, title, text, writer, regdate, updatedate
	from(
	  select /*+ INDEX_DESC (tbl_post pk_post) */
	    rownum rn, pono, title, text, writer, regdate, updatedate
	  from 
	    tbl_post
	  where
	    rownum <= #{pageNum} * #{amount}
	)
	where
	  rn > (#{pageNum} -1) * #{amount}
